import React, { useState } from "react";
import axios from "axios";

import Card from "./components/Card/Card";

function Test() {
  const [open, isOpen] = useState(true);
  const [recipes, setRecipes] = useState([]);
  const getRecipe = async (query) => {
    const response = await axios.get(
      `https://api.edamam.com/api/recipes/v2?type=public&q=${query}&app_id=48749e6b&app_key=%20e48b4d118c5df082474141e6e4746f1a`
    );
    console.log(response.data.hits);
    setRecipes(response.data.hits[0].recipe);
  };
  return (
    <div>
      <form
        autoComplete="off"
        onSubmit={(e) => {
          e.preventDefault();
          const username = e.target.elements.usernameInput.value;
          if (!username) {
            return alert("Please enter username");
          } else {
            return getRecipe(username);
          }
        }}
      >
        <input
          type="text"
          id="usernameInput"
          placeholder="Enter a username..."
        />
        <button type="submit">Submit</button>
      </form>
      <div className="recipes">
        {open
          ? recipes.map((recipe) => {
              <img src={recipe.image} />;
            })
          : "no results"}
      </div>
    </div>
  );
}

export default Test;
